import "package:flutter/services.dart";class NotificationService{static final NotificationService _i=NotificationService._();factory NotificationService()=>_i;NotificationService._();static const _channel=MethodChannel("com.mxui.vpn/notifications");Future<void> showConnectedNotification({required String server,required String ip})async{try{await _channel.invokeMethod("showConnected",{"server":server,"ip":ip});}catch(e){}}Future<void> showDisconnectedNotification()async{try{await _channel.invokeMethod("showDisconnected");}catch(e){}}Future<void> showTrafficNotification({required String download,required String upload})async{try{await _channel.invokeMethod("showTraffic",{"download":download,"upload":upload});}catch(e){}}Future<void> updateNotification({required String title,required String body})async{try{await _channel.invokeMethod("update",{"title":title,"body":body});}catch(e){}}Future<void> cancelNotification()async{try{await _channel.invokeMethod("cancel");}catch(e){}}}
